import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Home from './pages/Home';
import Cohorts from './pages/Cohorts';
import Classes from './pages/Classes';
import Projects from './pages/Projects';
import Admin from './pages/Admin';
import Login from './pages/Login';
import Register from './pages/Register';
import './App.css';

function App() {
  const [isAdmin, setIsAdmin] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/check_admin');
        const data = await response.json();

        if (response.ok) {
          console.log('User is authenticated and role checked'); // Debugging
          setIsAuthenticated(true);
          setIsAdmin(data.is_admin);
        } else {
          console.log('User is not authenticated or admin check failed'); // Debugging
          setIsAuthenticated(false);
          setIsAdmin(false);
        }
      } catch (error) {
        console.error('Error during authentication check:', error); // Debugging
        setIsAuthenticated(false);
        setIsAdmin(false);
      } finally {
        setLoading(false); // Stop loading after check
      }
    };

    checkAuthStatus();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Show a loading state until authentication is checked
  }

  const ProtectedRoute = ({ children }) => {
    return isAuthenticated ? children : <Navigate to="/login" />;
  };

  const AdminRoute = ({ children }) => {
    return isAdmin ? children : <Navigate to="/" />;
  };

  return (
    <Router>
      <div className="app">
        <Sidebar />
        <div className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cohorts" element={<ProtectedRoute><Cohorts /></ProtectedRoute>} />
            <Route path="/classes/:cohortId" element={<ProtectedRoute><Classes /></ProtectedRoute>} />
            <Route path="/projects/:classId" element={<ProtectedRoute><Projects /></ProtectedRoute>} />
            <Route path="/admin" element={<AdminRoute><Admin /></AdminRoute>} />
            <Route path="/login" element={isAuthenticated ? <Navigate to={isAdmin ? "/admin" : "/"} /> : <Login />} />
            <Route path="/register" element={isAuthenticated ? <Navigate to={isAdmin ? "/admin" : "/"} /> : <Register />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
